create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_BATCHTYPEEXTENSIONSPEC
(
    @ID uniqueidentifier output,
    @CHANGEAGENTID uniqueidentifier = null,
    @CONSID nvarchar(50) = null,
    @LASTLOGINDATE datetime = null,
    @CREATEDBY nvarchar(50) = null,
    @PRIMARYEMAIL nvarchar(200) = null,
    @DONOTEMAIL bit = null,
    @EMAILSTATUS nvarchar(100) = null,
    @OPTOUTDATE datetime = null,
    @OPTOUTMETHOD nvarchar(100) = null,
    @HOMEPHONE nvarchar(200) = null,
    @WORKPHONE nvarchar(200) = null,
    @CELLPHONE nvarchar(200) = null,
    @COUNTRYID uniqueidentifier = null,
    @ADDRESSBLOCK nvarchar(500) = null,
    @CITY nvarchar(200) = null,
    @STATEID uniqueidentifier = null,
    @ZIP nvarchar(200) = null
)
as
    set nocount on;

    if @ID is null
        set @ID = newid()

    if @CHANGEAGENTID is null  
        exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

    declare @CURRENTDATE datetime
    set @CURRENTDATE = getdate()

    begin try
        insert into USR_BATCHCONSTITUENTUPDATE_EXT(
            CONSID,
            LASTLOGINDATE,
            CREATEDBY,
            PRIMARYEMAIL,
            DONOTEMAIL,
            EMAILSTATUS,
            OPTOUTDATE,
            OPTOUTMETHOD,
            HOMEPHONE,
            WORKPHONE,
            CELLPHONE,
            COUNTRYID,
            ADDRESSBLOCK,
            CITY,
            STATEID,
            ZIP,
            ADDEDBYID,
            CHANGEDBYID,
            DATEADDED,
            DATECHANGED)
        values(
            @CONSID,
            @LASTLOGINDATE,
            @CREATEDBY,
            @PRIMARYEMAIL,
            @DONOTEMAIL,
            @EMAILSTATUS,
            @OPTOUTDATE,
            @OPTOUTMETHOD,
            @HOMEPHONE,
            @WORKPHONE,
            @CELLPHONE,
            @COUNTRYID,
            @ADDRESSBLOCK,
            @CITY,
            @STATEID,
            @ZIP,
            @CHANGEAGENTID,
            @CHANGEAGENTID,
            @CURRENTDATE,
            @CURRENTDATE)
    end try
    begin catch
        exec dbo.USP_RAISE_ERROR;
        return 1;
    end catch

return 0;

